mapper.select:
select * from product

	where subject like #{keyword}


	where contents like #{keyword}

order by no desc

mapper.insert:
insert into product values(
prdseq.nextval, #{seller_id}, #{subject}, #{category}, #{address}, #{image1}, 'dummy', 'dummy', #{contents}, #{price}, #{refund_availability}, sysdate, default, 0
)
